// frontend/src/components/layout/Sidebar.jsx
import React from "react";
import { Link, useLocation, NavLink } from "react-router-dom";
import {
  X,
  LayoutDashboard,
  Briefcase,
  CheckSquare,
  Calendar,
  FileText,
  ClipboardCheck,
  MessageSquare,
  Users,
  FileBox,
  Settings,
  UserCog,
  BarChart,
  List,
  Home,
} from "lucide-react";
import { useAuth } from "../../context/AuthContext";

const navigation = [
  { 
    name: "Dashboard", 
    to: "/dashboard", 
    icon: LayoutDashboard,
    color: "from-blue-600 to-blue-400"
  },
  { 
    name: "Surveys", 
    to: "/surveys", 
    icon: FileText,
    color: "from-purple-600 to-purple-400" 
  },
  { 
    name: "Roles & Permissions", 
    to: "/roles", 
    icon: Users,
    color: "from-green-600 to-green-400" 
  },
  { 
    name: "Reports", 
    to: "/reports", 
    icon: BarChart,
    color: "from-orange-600 to-orange-400" 
  },
  { 
    name: "Settings", 
    to: "/settings", 
    icon: Settings,
    color: "from-gray-600 to-gray-400" 
  },
];

const Sidebar = ({ open, setOpen }) => {
  const { user } = useAuth();
  const location = useLocation();

  return (
    <div
      className={`fixed inset-y-0 left-0 z-50 w-64 bg-white shadow-lg transition-transform duration-300 transform ${
        open ? "translate-x-0" : "-translate-x-full"
      } md:translate-x-0 md:static md:h-screen md:z-0`}
    >
      {/* Sidebar Header */}
      <div className="h-16 flex items-center justify-between px-4 bg-gradient-to-r from-indigo-700 to-purple-700">
        <Link
          to="/"
          className="flex items-center"
          onClick={() => setOpen && setOpen(false)}
        >
          <div className="h-10 w-10 rounded-full bg-white flex items-center justify-center">
            <Home className="h-6 w-6 text-indigo-700" />
          </div>
          <span className="ml-2 text-lg font-bold text-white">
            PMI Onboarding
          </span>
        </Link>

        {setOpen && (
          <button
            onClick={() => setOpen(false)}
            className="h-10 w-10 flex items-center justify-center rounded-md text-white hover:text-gray-200 focus:outline-none focus:ring-2 focus:ring-white md:hidden"
            aria-label="Close sidebar"
          >
            <X className="h-6 w-6" />
          </button>
        )}
      </div>

      {/* User Profile Summary */}
      <div className="px-4 py-4 border-b border-gray-200">
        <div className="flex items-center">
          <div className="h-10 w-10 rounded-full bg-gradient-to-r from-indigo-500 to-purple-500 flex items-center justify-center text-white font-bold">
            {user?.name?.charAt(0).toUpperCase() || "U"}
          </div>
          <div className="ml-3">
            <p className="text-sm font-medium text-gray-700">{user?.name}</p>
            <p className="text-xs text-gray-500 capitalize">{user?.role || "User"}</p>
          </div>
        </div>
      </div>

      {/* Navigation */}
      <div className="px-2 py-4">
        <nav className="space-y-2">
          {navigation.map((item) => (
            <NavLink
              key={item.name}
              to={item.to}
              className={({ isActive }) =>
                `group flex items-center px-3 py-3 rounded-lg transition-all duration-200 ${
                  isActive
                    ? `bg-gradient-to-r ${item.color} text-white shadow-md`
                    : "text-gray-700 hover:bg-gray-100"
                }`
              }
            >
              <item.icon className="mr-3 h-5 w-5 flex-shrink-0" />
              <span className="font-medium">{item.name}</span>
            </NavLink>
          ))}
        </nav>
      </div>

      {/* HR Admin Section */}
      {user?.role === "hr" && (
        <div className="mt-4 px-2 py-4 border-t border-gray-200">
          <h3 className="px-3 text-xs font-semibold text-gray-500 uppercase tracking-wider mb-3">
            Administration
          </h3>

          <nav className="space-y-2">
            <Link 
              to="/admin" 
              className="group flex items-center px-3 py-2 text-sm font-medium rounded-md text-gray-700 hover:bg-gray-100"
            >
              <LayoutDashboard className="mr-3 h-5 w-5 text-gray-500" />
              Admin Panel
            </Link>

            <Link 
              to="/admin/users" 
              className="group flex items-center px-3 py-2 text-sm font-medium rounded-md text-gray-700 hover:bg-gray-100"
            >
              <UserCog className="mr-3 h-5 w-5 text-gray-500" />
              User Management
            </Link>

            <Link 
              to="/admin/roles" 
              className="group flex items-center px-3 py-2 text-sm font-medium rounded-md text-gray-700 hover:bg-gray-100"
            >
              <Users className="mr-3 h-5 w-5 text-gray-500" />
              Roles & Permissions
            </Link>
          </nav>
        </div>
      )}

      {/* Footer */}
      <div className="absolute bottom-0 w-full p-4 border-t border-gray-200">
        <div className="flex items-center justify-between">
          <span className="text-xs text-gray-500">© 2025 PMI</span>
          <Link to="/help" className="text-xs text-indigo-600 hover:text-indigo-800">
            Help & Support
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
